// Add message to chat
function append(who, text) {
  const row = document.createElement('div');
  row.className = `row ${who}`;
  const bubble = document.createElement('div');
  bubble.className = `bubble ${who}`;
  bubble.textContent = text;
  row.appendChild(bubble);
  chatArea.appendChild(row);
  chatArea.scrollTo({ top: chatArea.scrollHeight, behavior: 'smooth' });
}

// Split long responses into chunks (maxWords = 75)
function splitResponse(text, maxWords = 75) {
  const words = text.split(" ");
  const parts = [];
  for (let i = 0; i < words.length; i += maxWords) {
    parts.push(words.slice(i, i + maxWords).join(" "));
  }
  return parts;
}

// Handle sending messages
form.addEventListener('submit', e => {
  e.preventDefault();
  const msg = inputEl.value.trim();
  if (!msg) return;
  append('user', msg);
  inputEl.value = '';

  // Typing indicator
  const typingRow = document.createElement('div');
  typingRow.className = 'row bot';
  const typingBubble = document.createElement('div');
  typingBubble.className = 'bubble bot';
  typingBubble.textContent = 'Typing...';
  typingRow.appendChild(typingBubble);
  chatArea.appendChild(typingRow);
  chatArea.scrollTo({ top: chatArea.scrollHeight, behavior: 'smooth' });

  // Determine bot response
  let response = "Hmm 🤔 I didn't understand that.";
  for (let key in RULES) {
    if (msg.toLowerCase() === key.toLowerCase()) {
      const responses = RULES[key];
      response = responses[Math.floor(Math.random() * responses.length)];
      break;
    }
  }

  // Split into message chunks
  const parts = splitResponse(response);
  let delay = Math.max(100, response.length * 10); // 30ms per character for realism

  // Remove typing animation before first message
  setTimeout(() => {
    chatArea.removeChild(typingRow);
    sendBotMessagesSequentially(parts);
  }, delay);
});

// Send multi-part bot messages with delay
function sendBotMessagesSequentially(parts) {
  parts.forEach((part, index) => {
    setTimeout(() => {
      append('bot', part);
    }, index * 1200); // 1.2s between parts
  });
}

// Avatar rotation logic
const avatarEl = document.querySelector('.avatar');
const avatarImages = [
    "images/im.png",
  "images/im1.png",
  "images/im2.png",
  "images/im3.png",
  "images/im4.png",
];
let avatarIndex = 0;

setInterval(() => {
  avatarIndex = (avatarIndex + 1) % avatarImages.length;
  avatarEl.style.backgroundImage = `url('${avatarImages[avatarIndex]}')`;
}, 5000);

// Avatar preview popup
const avatarPreview = document.getElementById('avatarPreview');
const previewImg = document.getElementById('previewImg');

avatarEl.addEventListener('click', () => {
  const currentBg = avatarEl.style.backgroundImage.slice(5, -2);
  previewImg.src = currentBg;
  avatarPreview.classList.add('active');
});

avatarPreview.addEventListener('click', (e) => {
  if (e.target === avatarPreview || e.target.classList.contains('overlay')) {
    avatarPreview.classList.remove('active');
  }
});
